#!/usr/bin/env node
import { program } from "commander";
import { getRemoteHomeUrl } from "../utils/git";
import { openUrl } from "../utils/system";

program
  .command("", { isDefault: true })
  .description("Open git project homepage")
  .action(run)
  .parse(process.argv);

async function run() {
  await getRemoteHomeUrl().then(openUrl);
}
